import React from 'react';
import ColumnList from '../Lists/ColumnList/ColumnList'
import Button, { BUTTON_SIZES, BUTTON_STYLES } from '../Button/Button'
import uuid from 'uuid/v4'
import AddInputControl from '../AddInputControl/AddInputControl';
import './ColumnContainer.scss';

class ColumnContainer extends React.Component {
    state = {
        columnName: '',
        columns: []
    }

    addColumn = () => {
        const newColumn = {
            id: uuid(),
            title: this.state.columnName,
            cards: []
        }
        const nuevoArray = this.state.columns.slice()
        nuevoArray.push(newColumn)
        this.setState({ columnName: '', columns: nuevoArray })
    }

    render() {
        // const { addColumn } = this.props
        return (
            <div className='column-container'>
                <div className='columns'>
                    <ColumnList columns={this.state.columns} />
                    <input
                        type='text'
                        className='text-input'
                        placeholder='Añada el nombre de la lista'
                        onChange={this.addColumnName}
                        value={this.state.columnName}

                    ></input>

                    <div onClick={this.addColumn}>
                        <Button
                            text='Añadir lista'
                            type={BUTTON_STYLES.DEFAULT}
                            size={BUTTON_SIZES.SHRINK}
                            className='action-button'
                        />
                    </div>

                </div>

            </div>
        )
    }
}

export default ColumnContainer;


